function populateDropdownForModels(){var n=makesList.selectedIndex,t;n==0?document.getElementById("models").innerHTML="":(t=makesList.options[n].value,$.ajax({type:"get",url:"/api/Models/ForMake",data:{makeId:t},success:function(n){var t=document.getElementById("models");t.innerHTML="";$.each(n,function(n,t){$("#models").append($("<option><\/option>").val(t.id).html(t.name))})}}))}var makesList=document.getElementById("makes");$("#makes").change(populateDropdownForModels);